def sendMessage(chat_id, text):
    URL = "https://api.telegram.org/bot"+token+"/sendmessage"
    PARAMS = {'chat_id': chat_id, 'text': text, 'parse_mode': 'markdown'}
    requests.get(url=URL, params=PARAMS)


